mapper.select:
SELECT COUNT(USER_ID) AS COUNT
FROM TBE_USER
  WHERE USER_ID = #{user_id}

mapper.insert:
INSERT INTO TBE_USER(
    USER_CD
    , USER_ID
    , USER_PASSWORD
    , USER_NAME
    , USER_USETODAY
    , USER_PHONE
    , USER_AGE
    , USER_ADDRESS
    , USER_TYPE_CODE
) VALUES (
    #{user_cd}
    , #{user_id}
    , #{user_password}
    , #{user_name}
    , '1'
    , #{user_phone}
    , #{user_age}
    , #{user_address}
    , #{user_type_code}
)
