mapper.select:
SELECT COUNT(USER_ID) AS COUNT
FROM TBE_USER
  WHERE USER_ID = #{user_id}

SELECT LPAD(NVL((SELECT COUNT(A.USER_CD) AS CNT
		from tbe_user A), 0)+1 ,5 ,'0')
FROM TBE_USER B

mapper.insert:
INSERT INTO TBE_USER(
    USER_CD
    , USER_ID
    , USER_PASSWORD
    , USER_NAME
    , USER_USETODAY
    , USER_PHONE
    , USER_PHONE_CHECK
    , USER_AGE
    , USER_ZIP_CODE
    , USER_ADDRESS
    , USER_TYPE_CODE
    , USER_SEX
    , USER_DATE
) VALUES (
    #{user_cd}
    , #{user_id}
    , #{user_password}
    , #{user_name}
    , '1'
    , #{user_phone}
    , 'Y'
    , #{user_age}
    , #{user_zip_code}
    , #{user_address}
    , #{user_type_code}
    , #{user_sex}
    , #{user_date}
)
